import { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Importa useNavigate en lugar de useHistory
import { registerUser } from '../services/authService';  // Asegúrate de tener este servicio
import '../styles/home.css';

const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();  // Usa useNavigate en lugar de useHistory

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await registerUser({ email, password });  // Asegúrate de que esta función esté bien implementada
      alert('Registro exitoso');
      navigate('/login');  // Redirige a la página de login después de registrar
    } catch (error) {
      alert('Error en el registro');
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Registrar una cuenta</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Correo electrónico"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Registrar</button>
      </form>
    </div>
  );
};

export default Register;
