import { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Importa useNavigate en lugar de useHistory
import { registerUser } from '../services/authService';  // Asegúrate de tener este servicio
import '../styles/login.css';

const Register = () => {
  const [formData, setFormData]= useState({
    name: '',
    email: '',
    password: '',
    user_type: '',
    faculty: '',
    career: ''
  });

  const navigate = useNavigate(); 

  const handleChange=(e) =>{
    const {name, value} = e.target;
    setFormData((prev)=>({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await registerUser({ email, password });  // Asegúrate de que esta función esté bien implementada
      alert('Registro exitoso');
      navigate('/login');  // Redirige a la página de login después de registrar
    } catch (error) {
      alert('Error en el registro');
      console.error(error);
    }
  };

  return (
    <div className="login-page">
      <div className="login-container">
        <div className="login-header">
          <h1>MarTutors</h1>
          <p>Crea una cuenta para continuar</p>
        </div>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Nombre completo"
            value={formData.name}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Correo electrónico"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Contraseña"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <select
            name="user_type"
            value={formData.user_type}
            onChange={handleChange}
            required
          >
            <option value="">Selecciona tu tipo de usuario</option>
            <option value="estudiante">Estudiante</option>
            <option value="egresado">Egresado</option>
            <option value="profesor">Profesor</option>
          </select>
          <input
            type="text"
            name="faculty"
            placeholder="Facultad"
            value={formData.faculty}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="career"
            placeholder="Carrera"
            value={formData.career}
            onChange={handleChange}
            required
          />
          <button type="submit" className="login-btn">Registrarse</button>
        </form>
        <div className="login-footer">
          <span>¿Ya tienes una cuenta?</span>
          <button onClick={() => navigate('/login')} className="register-btn">
            Inicia sesión aquí
          </button>
        </div>
      </div>
    </div>
  );
};

export default Register;
